import React, { useCallback, useState } from "react";
import Avatar from "@mui/material/Avatar";
import Button from "@mui/material/Button";
import CssBaseline from "@mui/material/CssBaseline";
import TextField from "@mui/material/TextField";
import Grid from "@mui/material/Grid";
import Box from "@mui/material/Box";
import Typography from "@mui/material/Typography";
import Container from "@mui/material/Container";
import { createTheme, ThemeProvider } from "@mui/material/styles";
import { PersonOutline } from "@material-ui/icons";
import useInput from "./useInput";
import { useNavigate } from "react-router-dom";
import { sendInsertReq } from "./UserApi";

const theme2 = createTheme();

function SignUpPage() {
  const navigate = useNavigate();

  const [imageSrc, setImageSrc] = useState("");
  const [id, onChangeId, setId] = useInput("");
  const [pwd, onChangePwd, setPwd] = useInput("");
  const [confirmPwd, onChangeConfirmPwd, setConfirmPwd] = useInput("");
  const [nickname, onChangeNickname, setNickname] = useInput("");
  const [name, onChangeName, setName] = useInput("");
  const [email, onChangeEmail, setEmail] = useInput("");
  const [phone, onChangePhone, setPhone] = useInput("");
  const [addr1, onChangeAddr1, setAddr1] = useInput("");
  const [addr2, onChangeAddr2, setAddr2] = useInput("");
  const [zipcode, onChangeZipcode, setZipcode] = useInput("");
  const [at, onChangeAt, setAt] = useInput("");

  const onSignUp = async (event) => {
    event.preventDefault();
    const data = new FormData(event.currentTarget);
    const info = await sendInsertReq(data);
    console.log("[SignUpPage-onSignUp]", info);
    if (info.result === "success") {
      alert("회원 가입이 완료되었습니다.");
      onReset();
      navigate("/login");
    } else {
      alert("회원 가입 에러");
    }
  };

  const onReset = useCallback(() => {
    setId("");
    setPwd("");
    setConfirmPwd("");
        setName("");
    setEmail("");
    setPhone("");
    setAddr1("");
    setAddr2("");
    setZipcode("");
    setAt("");
    setImageSrc("");
  }, [
    setId,
    setPwd,
    setConfirmPwd,
    setName,
    setEmail,
    setPhone,
    setAddr1,
    setAddr2,
    setZipcode,
    setAt,
  ]);

  // 사진 미리보기
  const encodeFileToBase64 = (fileBlob) => {
    const reader = new FileReader();
    reader.readAsDataURL(fileBlob);
    return new Promise((resolve) => {
      reader.onload = () => {
        setImageSrc(reader.result);
        resolve();
      };
    });
  };

  return (
    <ThemeProvider theme={theme2}>
      <Container component="main" maxWidth="xs">
        <CssBaseline />
        <Box
          sx={{
            marginTop: 8,
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
          }}
        >
          <Typography component="h1" variant="h5">
            회원가입
          </Typography>
          <form name="myform" encType="multipart/form-data" onSubmit={onSignUp}>
            <div
              style={{
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                flexDirection: "column",
                marginBottom: "20px",
              }}
            >
              <div
                style={{
                  width: "100px",
                  height: "100px",
                  display: "flex",
                  alignItems: "center",
                  justifyContent: "center",
                  flexDirection: "column",
                }}
              >
                {imageSrc && (
                  <img
                    src={imageSrc}
                    alt="preview-img"
                    style={{ width: "100px", padding: "10px" }}
                  />
                )}
                {!imageSrc && (
                  <Avatar
                    sx={{
                      m: 1,
                      width: 56,
                      height: 56,
                    }}
                  >
                    <PersonOutline />
                  </Avatar>
                )}
              </div>
              <div>
                <Button variant="contained" component="label">
                  사진 업로드
                  <input
                    id="file"
                    name="file"
                    type="file"
                    accept="img/*"
                    hidden
                    onChange={(e) => encodeFileToBase64(e.target.files[0])}
                  />
                </Button>
              </div>
            </div>
            <Grid container spacing={2}>
              <Grid item xs={12}>
                <TextField
                  autoComplete="아이디"
                  name="user_id"
                  required
                  fullWidth
                  id="user_id"
                  label="아이디"
                  autoFocus
                  value={id}
                  onChange={onChangeId}
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  name="password"
                  label="비밀번호"
                  type="password"
                  id="password"
                  autoComplete="password"
                  value={pwd}
                  onChange={onChangePwd}
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  label="비밀번호 확인"
                  type="cpassword"
                  autoComplete="cpassword"
                  value={confirmPwd}
                  onChange={onChangeConfirmPwd}
                  error={confirmPwd === pwd ? false : true}
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  name="nick_name"
                  label="닉네임"
                  id="nick_name"
                  autoComplete="nick_name"
                  value={nickname}
                  onChange={onChangeNickname}
                />
              </Grid>

              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  name="name"
                  label="이름"
                  id="name"
                  autoComplete="name"
                  value={name}
                  onChange={onChangeName}
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  name="email"
                  label="이메일"
                  id="email"
                  autoComplete="email"
                  value={email}
                  onChange={onChangeEmail}
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  name="phone"
                  label="전화번호"
                  id="phone"
                  autoComplete="phone"
                  value={phone}
                  onChange={onChangePhone}
                />
              </Grid>

              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  name="address1"
                  label="주소1"
                  id="address1"
                  autoComplete="address1"
                  value={addr1}
                  onChange={onChangeAddr1}
                />
              </Grid>

              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  name="address2"
                  label="주소2"
                  id="address2"
                  autoComplete="address2"
                  value={addr2}
                  onChange={onChangeAddr2}
                />
              </Grid>

              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  name="zipcode"
                  label="우편번호"
                  id="zipcode"
                  autoComplete="zipcode"
                  value={zipcode}
                  onChange={onChangeZipcode}
                />
              </Grid>

              <Grid item xs={12}>
                <TextField
                  fullWidth
                  name="at"
                  label="관리자번호"
                  id="at"
                  autoComplete="at"
                  value={at}
                  onChange={onChangeAt}
                />
              </Grid>
            </Grid>
            <Button
              type="submit"
              fullWidth
              variant="contained"
              sx={{ mt: 3, mb: 2 }}
            >
              등록
            </Button>
          </form>
        </Box>
      </Container>
    </ThemeProvider>
  );
}

export default SignUpPage;
